// src/services/api.js
import axios from "axios";

const API_BASE = process.env.REACT_APP_API_BASE || "http://localhost:4000/api";
const TENANT_ID =
  process.env.REACT_APP_TENANT_ID || "b9ea8bf3-f4e4-4dd9-9826-59967ffe974c"; // replace as needed

export async function fetchOverview() {
  const res = await axios.get(`${API_BASE}/stats/${TENANT_ID}/overview`);
  return res.data;
}

// Optional: fetch raw orders (if you implement server endpoint)
// Returns array of orders from backend
export async function fetchOrders() {
  const res = await axios.get(`${API_BASE}/orders/${TENANT_ID}/list`);
  return res.data;
}
